from django.core.management.base import BaseCommand
from user.models import User

class Command(BaseCommand):
    help = 'Create a superuser'

    def add_arguments(self, parser):
        parser.add_argument('email', type=str, help='Superuser email')
        parser.add_argument('fullname', type=str, help='Superuser fullname')
        parser.add_argument('password', type=str, help='Superuser password')

    def handle(self, *args, **kwargs):
        email = kwargs['email']
        fullname = kwargs['fullname']
        password = kwargs['password']

        # Check if the superuser already exists
        if User.objects.filter(email=email, is_superuser=True).count() > 0:
            self.stdout.write(self.style.WARNING("Superuser already exists."))
            return

        # Create a new superuser
        superuser = User(email=email, fullname=fullname, is_superuser=True, is_staff=True)
        superuser.setPassword(password)
        superuser.save()

        self.stdout.write(self.style.SUCCESS("Superuser created successfully."))